mapper.select:
select member_id, name
from member
where member_id = #{memberId}

select o.order_id, b.book_id, b.title, b.price, b.image_url, o.order_date
from orders o, book b
where b.book_id = o.registered_book_id and o.member_id = #{memberId}

select l.like_id, b.book_id, b.title, b.price, b.image_url
from likes l, book b
where b.book_id = l.registered_book_id and l.member_id = #{memberId}
