mapper.select:
select id, password, name, email, phone, store_name
from seller
where id=#{id} and password=#{password}

select id, password, name, email, phone, store_name
from seller
where id=#{id}

mapper.update:
update seller
set password=#{password}, store_name=#{store_name}, phone=#{phone}
WHERE id=#{id}

mapper.select:
select id
from seller
where name=#{name} and email=#{email}

select password
from seller
where id=#{id} and name=#{name} and email=#{email}
